import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Confetti from 'react-confetti';

type Heart = { id: number; left: number };

const BirthdaySite: React.FC = () => {
  const [introVisible, setIntroVisible] = useState(true);
  const [started, setStarted] = useState(false);
  const [musicOn, setMusicOn] = useState(false);
  const [showEnvelope, setShowEnvelope] = useState(false);
  const [candlesLit, setCandlesLit] = useState(true);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showMessagePopup, setShowMessagePopup] = useState(false);
  const [popMessage, setPopMessage] = useState('');
  const [hearts, setHearts] = useState<Heart[]>([]);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const [lightboxIndex, setLightboxIndex] = useState<number>(-1);

  const birthDate = new Date(2007, 10, 20);
  const [daysAlive, setDaysAlive] = useState<number>(0);
  const [countdown, setCountdown] = useState({ d: 0, h: 0, m: 0, s: 0 });

  const gallery = [
    '/assets/ourfirstpic.jpg',
    '/assets/2nd_pic.jpg',
    '/assets/3rdpic.jpg',
    '/assets/4thpic.jpg',
    '/assets/7thpic.jpg',
    '/assets/6thpic.jpg',
  ];

  useEffect(() => {
    const update = () => {
      const now = new Date();
      const diffDays = Math.floor((now.getTime() - birthDate.getTime()) / (1000 * 60 * 60 * 24));
      setDaysAlive(diffDays >= 0 ? diffDays : 0);

      const year = (now.getMonth() > birthDate.getMonth() || (now.getMonth() === birthDate.getMonth() && now.getDate() > birthDate.getDate()))
        ? now.getFullYear() + 1
        : now.getFullYear();
      const next = new Date(year, birthDate.getMonth(), birthDate.getDate(), 0, 0, 0);
      const diff = Math.max(0, next.getTime() - now.getTime());
      const d = Math.floor(diff / (1000 * 60 * 60 * 24));
      const h = Math.floor((diff / (1000 * 60 * 60)) % 24);
      const m = Math.floor((diff / (1000 * 60)) % 60);
      const s = Math.floor((diff / 1000) % 60);
      setCountdown({ d, h, m, s });
    };

    update();
    const id = setInterval(update, 1000);
    return () => clearInterval(id);
  }, [birthDate]);

  useEffect(() => {
    const id = setInterval(() => {
      setHearts(h => [...h, { id: Date.now(), left: Math.random() * 92 }].slice(-6));
    }, 1400);
    return () => clearInterval(id);
  }, []);

  useEffect(() => {
    if (!audioRef.current) return;
    if (musicOn && started) {
      audioRef.current.play().catch(() => { });
    } else {
      audioRef.current.pause();
    }
  }, [musicOn, started]);

  const balloonMessages = [
    "I love you üòò",
    "You make my world brighter üíó",
    "You‚Äôre the best thing that ever happened¬†to¬†me üíû",
    "Happy 18th, My love üéÄ",
    "You + me = ‚ù§Ô∏è",
    "Thank you for being the reason my life feels beautifully meant üå∏",
  ];

  function popBalloon() {
    const msg = balloonMessages[Math.floor(Math.random() * balloonMessages.length)];
    setPopMessage(msg);
    setShowMessagePopup(true);
    setShowConfetti(true);
    setTimeout(() => setShowConfetti(false), 3300);
  }

  function blowCandles() {
    if (!candlesLit) return;
    setCandlesLit(false);
    setShowConfetti(true);
    setTimeout(() => setShowConfetti(false), 3500);
  }

  const [popupImage, setPopupImage] = useState<string | null>(null);

  useEffect(() => {
    gallery.forEach(s => { const i = new Image(); i.src = s; });
    const p = new Image(); p.src = '/assets/Her_portrait.jpg';
  }, []);

  const handleStart = () => {
    setStarted(true);
    setMusicOn(true);
    setTimeout(() => setIntroVisible(false), 1200);
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-b from-[#FFF1F8] to-[#FFC6E5] text-gray-900 antialiased">
      <audio ref={audioRef} src="/assets/flute_bg.mp3" loop />

      <AnimatePresence>
        {introVisible && (
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-50 flex items-center justify-center">
            <div className="absolute inset-0 bg-gradient-to-b from-[#FFEAF2] to-[#FFD1E6] opacity-95" />
            <div className="relative z-10 px-6 py-8 max-w-2xl w-[92%] text-center rounded-2xl bg-white/60 backdrop-blur-sm shadow-2xl">
              <motion.h1 initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.8 }} className="text-4xl md:text-5xl font-bold text-[#FF5CA2] tracking-tight">
                Happy 18th Birthday, <span className="text-pink-600">Divya</span> üíñ
              </motion.h1>
              <motion.p initial={{ opacity: 0 }} animate={{ opacity: 0.95 }} transition={{ delay: 0.25 }} className="mt-4 text-gray-700">
                A tiny website for a big heart. Tap around ‚Äî there are little surprises hidden all over. üíñ
              </motion.p>
              <div className="mt-6 flex items-center justify-center gap-4">
                <button onClick={handleStart} className="px-5 py-2 rounded-full bg-gradient-to-r from-[#FF8DBE] to-[#FF5CA2] text-white font-semibold shadow-lg hover:scale-[1.03] transition">
                  Start & Play Music
                </button>
                <button onClick={() => setIntroVisible(false)} className="px-4 py-2 rounded-full bg-white border border-pink-200 text-pink-600 shadow">
                  Skip
                </button>
              </div>
              <div className="mt-6 text-sm text-gray-500">
                <div>Born: <strong className="text-gray-800">20 Nov 2007</strong></div>
                <div className="mt-1">Days alive: <strong>{daysAlive}</strong></div>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      <div className={`${introVisible ? 'pointer-events-none opacity-40' : 'opacity-100'} transition-all`}>
        <header className="max-w-5xl mx-auto py-6 px-4 flex items-center justify-between">
          <div>
            <h2 className="text-2xl md:text-3xl font-extrabold text-[#FF6AA5]">Happy 18th Birthday ‚Äî Divya</h2>
            <p className="text-sm text-gray-700 mt-1">Made with ‚ù§Ô∏è ‚Äî A small surprise for you</p>
          </div>
          <div className="flex items-center gap-3">
            <button onClick={() => { setMusicOn(m => !m); setStarted(true); }} className="px-3 py-2 rounded-full bg-white/80 text-sm shadow-sm">
              {musicOn ? 'Music On' : 'Music Off'}
            </button>
            <button onClick={() => window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' })} className="px-3 py-2 rounded-full bg-pink-100 text-sm shadow-sm">
              Surprise ‚ù§Ô∏è
            </button>
          </div>
        </header>

        <main className="max-w-5xl mx-auto px-4 pb-20">
          <section className="bg-white/60 backdrop-blur rounded-2xl p-6 md:p-8 shadow-md grid md:grid-cols-2 gap-6 items-center">
            <div>
              <motion.h3 initial={{ y: 16, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.6 }} className="text-2xl md:text-3xl font-bold">
                Happy 18th, <span className="text-[#FF5CA2]">Divya</span>
              </motion.h3>
              <p className="mt-3 text-gray-700">A tiny website for a big heart ‚Äî tap around to find little surprises.</p>
              <div className="mt-5 flex flex-wrap gap-3">
                <button onClick={() => setShowEnvelope(true)} className="px-4 py-2 rounded-full bg-gradient-to-r from-[#FF8DBE] to-[#FF5CA2] text-white shadow">Open My Letter</button>
                <button onClick={() => window.scrollTo({ top: 700, behavior: 'smooth' })} className="px-4 py-2 rounded-full bg-white border">See Gallery</button>
              </div>
              <div className="mt-6 grid grid-cols-3 gap-3 max-w-xs">
                <div className="bg-white p-3 rounded-lg shadow text-center">
                  <div className="text-lg font-bold text-[#FF5CA2]">{daysAlive}</div>
                  <div className="text-xs text-gray-500">Days alive</div>
                </div>
                <div className="bg-white p-3 rounded-lg shadow text-center">
                  <div className="text-lg font-bold text-[#FF5CA2]">18</div>
                  <div className="text-xs text-gray-500">Years</div>
                </div>
                <div className="bg-white p-3 rounded-lg shadow text-center">
                  <div className="text-lg font-bold text-[#FF5CA2]">‚àû</div>
                  <div className="text-xs text-gray-500">Love</div>
                </div>
              </div>
            </div>
            <div className="flex items-center justify-center">
              <motion.div drag dragConstraints={{ top: -8, bottom: 8, left: -8, right: 8 }} className="w-48 h-48 md:w-64 md:h-64 rounded-2xl overflow-hidden shadow-2xl bg-gradient-to-br from-pink-50 to-pink-100 p-2">
                <img src="/assets/5thpic.jpg" alt="Divya portrait" className="w-full h-full object-cover rounded-xl object-[center_48%]" />
              </motion.div>
            </div>
          </section>

          <section id="gallery" className="mt-8">
            <h4 className="text-xl font-semibold text-gray-800">Our Moments</h4>
            <p className="text-sm text-gray-600 mt-1">Tap images to enlarge ‚ú®</p>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3 mt-4">
              {gallery.map((g, i) => (
                <motion.div key={g} whileHover={{ scale: 1.02 }} onClick={() => setPopupImage(g)} className="rounded-lg overflow-hidden shadow cursor-pointer">
                  <img src={g} alt={`photo-${i}`} className="w-full h-40 object-cover" />
                </motion.div>
              ))}
            </div>
          </section>

          <section className="mt-8 bg-white/60 p-4 rounded-xl shadow">
            <div className="flex items-start justify-between gap-4">
              <div>
                <h5 className="text-lg font-semibold text-[#FF5CA2]">Cake & Surprise</h5>
                <p className="text-sm text-gray-600">Make a wish and blow the candles üéÇ</p>
              </div>
              <div className="flex gap-2">
                <button onClick={blowCandles} className="px-3 py-2 rounded-full bg-pink-100">Blow Candles</button>
                <button onClick={popBalloon} className="px-3 py-2 rounded-full bg-[#FF5CA2] text-white">Pop Balloon</button>
              </div>
            </div>

            <div className="mt-4 grid grid-cols-1 md:grid-cols-2 gap-4 items-center">
              <div className="flex items-center justify-center">
                <div className="w-52 h-36 bg-gradient-to-b from-pink-200 to-pink-300 rounded-xl flex items-center justify-center relative">
                  <div className="text-2xl font-bold">18</div>
                  <div className="absolute bottom-4 flex gap-2">
                    {[0, 1, 2].map(i => (
                      <div key={i} className="w-2 h-8 bg-yellow-300 rounded-sm relative">
                        {candlesLit && <span className="absolute -top-4 left-1/2 -translate-x-1/2 text-sm animate-bounce">üî•</span>}
                      </div>
                    ))}
                  </div>
                </div>
              </div>
              <div>
                <div className="grid grid-cols-3 gap-2">
                  {[...Array(6)].map((_, i) => (
                    <button key={i} onClick={popBalloon} className="p-3 bg-pink-50 rounded-lg">üéà</button>
                  ))}
                </div>
              </div>
            </div>
          </section>

          <section className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-3">
            <div className="p-4 bg-white rounded-lg shadow text-center">
              <div className="text-2xl font-bold text-[#FF5CA2]">{countdown.d}</div>
              <div className="text-sm text-gray-500">Days until next birthday</div>
            </div>
            <div className="p-4 bg-white rounded-lg shadow text-center">
              <div className="text-2xl font-bold text-[#FF5CA2]">{countdown.h}</div>
              <div className="text-sm text-gray-500">Hours</div>
            </div>
            <div className="p-4 bg-white rounded-lg shadow text-center">
              <div className="text-2xl font-bold text-[#FF5CA2]">{countdown.m}:{String(countdown.s).padStart(2, "0")}</div>
